import React, { useState } from "react";
import "./components/Patient.css";

const Patient = () => {
  const [formData, setFormData] = useState({
    name: "",
    date: "",
    problemStart: "",
    describeProblem: "",
    cause: "",
    surgery: "",
    surgeryDate: "",
    medicalHistory: [],
    allergies: [],
    additionalComments: "",
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === "checkbox" && e.target.dataset.group === "medicalHistory") {
      setFormData((prev) => {
        const newHistory = checked
          ? [...prev.medicalHistory, value]
          : prev.medicalHistory.filter((item) => item !== value);
        return { ...prev, medicalHistory: newHistory };
      });
    } else if (type === "checkbox" && e.target.dataset.group === "allergies") {
      setFormData((prev) => {
        const newAllergy = checked
          ? [...prev.allergies, value]
          : prev.allergies.filter((item) => item !== value);
        return { ...prev, allergies: newAllergy };
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Patient Information:", formData);
    alert("Patient information submitted successfully!");
  };

  return (
    <div className="patient-container">
      <h1 className="form-title">Patient Information Form</h1>

      <form onSubmit={handleSubmit} className="patient-form">
        {/* Basic Info */}
        <div className="form-row">
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Date:</label>
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="form-group">
          <label>When did your problem start?</label>
          <input
            type="text"
            name="problemStart"
            value={formData.problemStart}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Describe Problem:</label>
          <textarea
            name="describeProblem"
            value={formData.describeProblem}
            onChange={handleChange}
          ></textarea>
        </div>

        {/* Cause */}
        <div className="form-group">
          <label>Cause of Current Problem:</label>
          <div className="checkbox-grid">
            {["Car Accident", "Work injury", "Gradual onset", "Other"].map(
              (cause) => (
                <label key={cause}>
                  <input
                    type="radio"
                    name="cause"
                    value={cause}
                    checked={formData.cause === cause}
                    onChange={handleChange}
                  />{" "}
                  {cause}
                </label>
              )
            )}
          </div>
        </div>

        {/* Surgery */}
        <div className="form-group">
          <label>Did this problem require surgery?</label>
          <div className="checkbox-grid">
            {["No", "Yes"].map((s) => (
              <label key={s}>
                <input
                  type="radio"
                  name="surgery"
                  value={s}
                  checked={formData.surgery === s}
                  onChange={handleChange}
                />{" "}
                {s}
              </label>
            ))}
          </div>

          {formData.surgery === "Yes" && (
            <div className="form-group">
              <label>Date of Surgery:</label>
              <input
                type="date"
                name="surgeryDate"
                value={formData.surgeryDate}
                onChange={handleChange}
              />
            </div>
          )}
        </div>

        {/* Medical History */}
        <div className="form-group">
          <label>Do you have a history of the following problems?</label>
          <div className="checkbox-grid">
            {[
              "Breathing Problems",
              "Heart Problems",
              "Diabetes",
              "Stroke",
              "Kidney Problems",
              "Gallbladder/Liver",
              "Depression",
              "Bone/Joint Problems",
              "Drug use",
              "Smoking",
              "Headaches",
              "Anxiety attacks",
            ].map((item) => (
              <label key={item}>
                <input
                  type="checkbox"
                  data-group="medicalHistory"
                  value={item}
                  checked={formData.medicalHistory.includes(item)}
                  onChange={handleChange}
                />{" "}
                {item}
              </label>
            ))}
          </div>
        </div>

        {/* Allergies */}
        <div className="form-group">
          <label>Allergies:</label>
          <div className="checkbox-grid">
            {["Latex", "Iodine", "Bromine", "No Known allergies"].map((a) => (
              <label key={a}>
                <input
                  type="checkbox"
                  data-group="allergies"
                  value={a}
                  checked={formData.allergies.includes(a)}
                  onChange={handleChange}
                />{" "}
                {a}
              </label>
            ))}
          </div>
        </div>

        {/* Comments */}
        <div className="form-group">
          <label>Additional Comments:</label>
          <textarea
            name="additionalComments"
            value={formData.additionalComments}
            onChange={handleChange}
          ></textarea>
        </div>

        <div className="submit-container">
          <button type="submit" className="submit-btn">
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default Patient;
